import React, { Component } from 'react'

export default class Products extends Component {
    state = {
        user: null
      }
      componentDidMount () {
        const { slug } = this.props.match.params
    
        fetch(`https://api.twitter.com/user/${slug}`)
          .then((user) => {
            this.setState(() => ({ user }))
          })
      }
      render() {
        return(<>{ slug }</>);
      }
}
